    e.preventDefault(); 
    e.stopPropagation();
    
    dispatch(
      addToCart({
        id: product._id,
        name: product.name,
        price: product.price,
        image: displayImage,
        quantity: 1,
      })
    );
  };

  // Construct dynamic URL: /products/Category/Slug
  // Ensure category is safe for URL (optional safety check)
  const categoryPath = product.category ? encodeURIComponent(product.category) : 'Uncategorized';
