              {/* Main Image */}
              <div className='flex-1 border border-gray-200 rounded-xl overflow-hidden bg-gray-50 relative h-[400px] md:h-[500px]'>
                <img
                  src={thumbnail}
                  alt={product.name}
                  className='w-full h-full object-contain mix-blend-multiply p-4'
